import Vue from 'vue'
import VueRouter from 'vue-router'
import Index from "@/views/common/Index";
import UserList from "@/views/user/list"
import UserAdd from "@/views/user/add"
import UserUpdate from "@/views/user/update"
import Layout from "@/views/common/layout"

Vue.use(VueRouter);

const routes = [
  {
    path: '/login',
    component: () => import("../views/common/login")
  },
  {
    path: '/enroll',
    component: () => import("../views/common/enroll")
  },
  {
    path: '/',
    component: Layout,
    redirect: '/home',
    children: [
      {
        path: 'home',
        component: Index
      },
      {
        path: 'stu/list',
        name: 'StuList',
        component: () => import("../views/stu/searchList")
      },
      //---------------------------user------------------------
      {
        path: 'user/list',
        name: 'UserList',
        component: UserList
      },
      {
        path: 'user/add',
        name: 'UserAdd',
        component: UserAdd
      },
      {
        path: 'user/update',
        name: 'UserUpdate',
        component: UserUpdate
      },
      {
        path: 'user/info',
        component: () => import("../views/common/info")
      },
      // 其他路由保持不变...
    ]
  },
];

const router = new VueRouter({
  mode: 'history',
  routes
});

import Cookies from 'js-cookie';

router.beforeEach((to, from, next) => {
  if (to.path === '/login' || to.path === '/enroll' || to.path === '/forgetPassword') {
    return  next();
  }
  let user = Cookies.get('user');
  if (user == null && (to.path !== '/login' || to.path !== '/enroll' || to.path !== '/forgetPassword')){
    return next('/login');
  }
  next();
});

export default router
